	private ProfileRepository profileRepository;

	@InjectMocks
	ArtService artService;

	private long id = 0;
	private HashSet<Art> savedArt = new HashSet<>();
	private HashSet<Profile> savedProfile = new HashSet<>();

	@BeforeEach
	public void setupMockup() {
		// address id generator mock
		id = 0;

		// database mock
		savedArt.clear();
		savedProfile.clear();

		// lambda for simulating saving Art
		Answer<Art> updateConfirmationNumberAndReturn = (InvocationOnMock invocation) -> {
			Art art = (Art) invocation.getArgument(0);
			art.setId(id);
			savedArt.add(art);
			id++;
			return art;
		};

		// lambda for simulating saving Profile
		Answer<Profile> saveProfileAndReturn = (InvocationOnMock invocation) -> {
			Profile profile = (Profile) invocation.getArgument(0);
			savedProfile.add(profile);
			return profile;
		};

		// simulate saving art
		lenient().when(artRepository.save(any(Art.class))).thenAnswer(updateConfirmationNumberAndReturn);

		// simulate saving profile
		lenient().when(profileRepository.save(any(Profile.class))).thenAnswer(saveProfileAndReturn);

		// simulate finding Art by Id
		lenient().when(artRepository.findById(any(Long.class))).thenAnswer((InvocationOnMock invocation) -> {
			long id = ((long) invocation.getArgument(0));
			for (Art art : savedArt) {
				if (art.getId().equals(id))
					return Optional.ofNullable(art);
			}
			return Optional.empty();
		});
				
		// simulate finding all art
		lenient().when(artRepository.findAll()).thenAnswer((InvocationOnMock invocation) -> {
			return savedArt;
		});
	}
	
	/**
	 * Tests to see if an Art can be created using using createArt method
	 */
	@Test
	void testSuccesfullArt() {
		String title = "McGill";
		String description = "A painting of McGill";
		double height = 10.0;
		double width = 10.0;
		double depth = 10.0;
		String image = "data:image/jpeg;base64,";
		Date creationDate = new Date(26102020);
		Profile publisher = new Profile();
		profileRepository.save(publisher);
		String type = "abstract";
		String author = "Thomas";

		Optional<Art> art1 = artService.createArt(title, description, height, width, depth, image, creationDate,
				publisher, type, author);

		assertTrue(art1.isPresent());
		
		title = "Concordia";
		description = "A painting of Concordia";
		height = 8.0;
		width = 8.0;
		depth = 8.0;
		image = "data:image/;base64,";
		creationDate = new Date(26102020);
		publisher = new Profile();
		profileRepository.save(publisher);
		type = "crayons";
		author = "Daniel";
		
		Optional<Art> art2 = artService.createArt(title, description, height, width, depth, image, creationDate,
				publisher, type, author);

		assertTrue(art2.isPresent());
	}

	/**
	 * Tests to see if an Art is not created given an invalid title
	 */
	@Test
	void testTitleViolation() {
		// art with chosen title matches already existing title
		assertEquals(0, artService.getAllArt().size());
		String title = "McGill";
		String description = "A painting of McGill";
		double height = 10.0;
		double width = 10.0;
		double depth = 10.0;
		String image = "data:image/jpeg;base64,";
		Date creationDate = new Date(26102020);
		Profile publisher = new Profile();
		String type = "abstract";
		String author = "Thomas";

		Optional<Art> art1 = artService.createArt(title, description, height, width, depth, image, creationDate,
				publisher, type, author);

		assertEquals(1, artService.getAllArt().size());

		title = "McGill";
		description = "A bigger painting of McGill";
		height = 20.0;
		width = 20.0;
		depth = 20.0;
		image = "data:image/jpeg;base64,";
		creationDate = new Date(27102020);
		type = "more_abstract";
		author = "Anthony";

		Optional<Art> art2 = artService.createArt(title, description, height, width, depth, image, creationDate,
				publisher, type, author);

		assertEquals(1, artService.getAllArt().size());
		
		title = null;
		description = "A painting of McGill";
		height = 10.0;
		width = 10.0;
		depth = 10.0;
		image = "data:image/jpeg;base64,";
		creationDate = new Date(26102020);
		publisher = new Profile();
		type = "abstract";
		author = "Thomas";

		Optional<Art> art3 = artService.createArt(title, description, height, width, depth, image, creationDate,
				publisher, type, author);

		assertEquals(1, artService.getAllArt().size());

		title = "";
		description = "A bigger painting of McGill";
		height = 20.0;
		width = 20.0;
		depth = 20.0;
		image = "data:image/jpeg;base64,";
		creationDate = new Date(27102020);
		type = "more_abstract";
		author = "Anthony";

		Optional<Art> art4 = artService.createArt(title, description, height, width, depth, image, creationDate,
				publisher, type, author);

		assertEquals(1, artService.getAllArt().size());
	}
	
